let play=document.getElementById("play");
let previous=document.getElementById("previous");
let next=document.getElementById("next");
let loops=document.getElementById("loop");
let audio=document.getElementById("audio");
let length=document.getElementById("length");
let heading=document.getElementById("head");
let images=document.getElementById("image");

const audios=["soni soni.mp3", "shinunoga_e_wa.mp3", "Thanu.mp3"];
const imgs=["data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"];
const names=["Soni Soni","Shinunoga_e_wa","Thanu"];            
let index=0;
images.src=imgs[index];
audio.src=audios[index];

next.addEventListener("click",function(){
    if(index<audios.length-1)
    {
        ++index;
    }
    else
    {
        index=0;
    }
    audio.src=audios[index];
    heading.textContent=names[index];
    console.log(index);
    images.src=imgs[index];
})

previous.addEventListener("click",function(){
    if(index===0)
    {
        index=audios.length-1;
    }
    else
    {
        --index;
    }
    audio.src=audios[index];
    heading.textContent=names[index];
    images.src=imgs[index];
    console.log(index);
})

play.addEventListener("click",function(){
    if(audio.paused)
    {
        audio.play();
    }
    else
    {
        audio.pause();
    }
})

loops.addEventListener("click",function(){
    audio.loop=!audio.loop;
    if(audio.loop)
    {
        console.log("song is in loop");
    }
    else
    {
        console.log("looping disabled");
    }
})

audio.ontimeupdate=function()
{
    let percent;
    if(audio.duration)
    {
        percent=(audio.currentTime/audio.duration)*100;
        length.style.width=percent+"px";
        let current = Math.floor(audio.currentTime);
        let min = Math.floor(current / 60);
        let sec = current % 60;
        document.getElementById("min").textContent = min.toString().padStart(2, "0");
        document.getElementById("sec").textContent = sec.toString().padStart(2, "0");
    }
}